import React from 'react'
import { useState } from 'react'
var data = require('./SearchData.json');
function SearchBar() {

    const [value, setValue] = useState('');
    const onChange = (event) => {
        setValue(event.target.value);
    }
    const onSearch = (searchVal) => {
        setValue(searchVal);
        console.log(searchVal)
    }
  return (
    <div className="search-container">
        <div className="search-input">
            <input type="text" value={value} onChange={onChange}/>
            <button onClick={onSearch(value)}> Lock in</button>
        </div>
        <div className="drop-down">
            {data.filter(item => {
                const searchItem = value.toLowerCase();
                const fullName = item.hi.toLowerCase();
                return searchItem && fullName.startsWith(searchItem);
            })
            .map((item)=> (<div onClick={()=>onSearch(item.fullName)}className='drop-down-item'>{item.hi}</div>))}
        </div>

    </div>
  )
}

export default SearchBar
